import { Box } from "@mui/system";
import { Button } from "@mui/material";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import AccountCircle from "@mui/icons-material/AccountCircle";
import PasswordIcon from "@mui/icons-material/Password";
import EmailIcon from "@mui/icons-material/Email";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
import { Navigate } from "react-router-dom";


const Register = () => {
  const navigate = useNavigate();
  const [name, setName] = useState( "" );
  const [password, setPassword] = useState ( "" )
  const [email, setEmail] = useState ("")

 const registerHandler = (event) => {
     event.preventDefault()
    axios
      .post('http://localhost:3000/register', {
        name: name,
        email: email,
        password: password
      })
      .then((res) => res.data)
      .then(() => {
        navigate('/login');
        alert('Congratulations successful registration!');
      })
      .catch(() => alert('Wrong registration, please try again'));
  }



   const handleName = (event) => {
       setName(event.target.value)
        };

   const handlePassword = (event) => {
        setPassword(event.target.value)
        };

   const handleEmail = (event) => {
        setEmail(event.target.value)
         };

    const handleSubmit = (event) => {
      event.preventDefault()
      axios.post(`http://localhost:3001/register`,{name,password,email}).then((res)=>{ Navigate("/register")})

    };


  return (
    <form onSubmit={handleSubmit}>
    <Box
      component="form"
      sx={{
        "& .MuiTextField-root": { m: 1, width: "25ch" },
      }}
      noValidate
      autoComplete="off"
    >
      <div className="container">
        <h2 className="title">
          Ingrese todos los datos para completar el registro:
        </h2>

        <div className="div-input">
          <TextField
            required
            id="name"
            label="Nombre de usuario"
            variant="filled"
            name={name}
            onChange={handleName}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <AccountCircle />
                </InputAdornment>
              ),
            }}
          />
        </div>
        <div className="div-input">
          <TextField
            required
            id="password"
            label="Contraseña"
            type="password"
            autoComplete="current-password"
            variant="filled"
            name={password}
            onChange={handlePassword}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <PasswordIcon />
                </InputAdornment>
              ),
            }}
          />
        </div>
        <div className="div-input">
          <TextField
            required
            label="Email"
            type="email"
            id="email"
            variant="filled"
            name={email}
            onChange={handleEmail}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <EmailIcon />
                </InputAdornment>
              ),
            }}
          />
        </div>
        <div className="div-input" id="last-div">
          <div>
            <Button
              className="enviar-datos"
              variant="contained"
              onClick={registerHandler}
            >
              Registrarme
            </Button>
          </div>
        </div>
      </div>
    </Box>
    </form>
  );
};

export default Register;
